package org.javaworld.cmsbackend.service;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertNotNull;
import static org.junit.Assert.assertNull;
import static org.junit.Assert.assertTrue;

import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;

import javax.validation.ConstraintViolationException;

import org.javaworld.cmsbackend.entity.Category;
import org.javaworld.cmsbackend.entity.Product;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.autoconfigure.jdbc.AutoConfigureTestDatabase;
import org.springframework.boot.test.autoconfigure.jdbc.AutoConfigureTestDatabase.Replace;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.test.context.junit4.SpringRunner;
import org.javaworld.cmsbackend.CmsBackEndApplication;
import org.javaworld.cmsbackend.constants.Constants;

@RunWith(SpringRunner.class)
@SpringBootTest
@AutoConfigureTestDatabase(replace = Replace.NONE)
public class ProductServiceIntegrationTest {

	@Autowired
	ProductService productService;
	
	@Autowired
	CmsBackEndApplication cmsBackEndApplication;

	@Test
	public void save_product_without_image() {

		Category c = new Category();
		c.setId(2);
		
		Product p = new Product();
		p.setName("spaghetti");
		p.setPrice(20.35);
		p.setActive(true);
		p.setDescription("nice food");
		p.setCategory(c);
		p.setVersion(5);  //random value
		
		
		Product savedProduct = productService.saveProduct(p);

		assertNotNull(savedProduct.getId());
		assertEquals(p.getName(), savedProduct.getName());
		assertEquals(p.getDescription(), savedProduct.getDescription());
		assertEquals(p.getPrice(), savedProduct.getPrice(), 0);
		assertEquals(p.getActive(), savedProduct.getActive());
		assertEquals(Constants.NOT_FOUND_IMAGE_NAME, savedProduct.getImageName());	
		assertNull(savedProduct.getBase64Image());
		assertEquals(2, savedProduct.getCategory().getId(), 0);
		assertEquals(0, savedProduct.getVersion());
	}
	
	@Test
	public void save_product_with_image() {

		Category c = new Category();
		c.setId(2);
		
		Product p = new Product();
		p.setName("spaghetti");
		p.setPrice(20.35);
		p.setActive(true);
		p.setDescription("nice food");
		p.setCategory(c);
		p.setBase64Image("data:image/jpeg;base64,");
		p.setVersion(5);  //random value

		Product savedProduct = productService.saveProduct(p);

		assertNotNull(savedProduct.getId());
		assertEquals(p.getName(), savedProduct.getName());
		assertEquals(p.getDescription(), savedProduct.getDescription());
		assertEquals(p.getPrice(), savedProduct.getPrice(), 0);
		assertEquals(p.getActive(), savedProduct.getActive());		
		assertEquals(true, savedProduct.getImageName().matches("[0-9]+\\.jpg$"));
		assertNull(savedProduct.getBase64Image());
		assertEquals(2, savedProduct.getCategory().getId(), 0);
		
		String filePath = cmsBackEndApplication.getProjectFilesLocation()
				+ File.separator + "products/products_images" 
				+ File.separator + savedProduct.getImageName();
		
		assertTrue("file not found at path: " + filePath, Files.exists(Paths.get(filePath)));
		assertEquals(0, savedProduct.getVersion());
		
	}
	
	@Test(expected = ConstraintViolationException.class)
	public void save_product_without_catgeory_it_should_throw_exception() {
		Product p = new Product();
		p.setName("spaghetti");
		p.setPrice(20.35);
		p.setActive(true);
		p.setDescription("nice food");		
		productService.saveProduct(p);	
	}
	
	@Test
	public void update_product() {
		Product p = productService.getProductById(2);
		p.setName("testName");
		p.setDescription("test desc");
		p.setPrice(p.getPrice() * 2 + 5);
		p.setActive(true);
		/**
		 * write tests for following fields:-
		 * 1- image name
		 * 2- base64 string
		 * 3- category
		 */
		
		Product updatedProduct = productService.updateProduct(p);
		assertEquals(p.getName(), updatedProduct.getName());
		assertEquals(p.getDescription(), updatedProduct.getDescription());
		assertEquals(p.getPrice(), updatedProduct.getPrice(), 0);
		assertEquals(p.getActive(), updatedProduct.getActive());
		assertEquals(p.getVersion() + 1, updatedProduct.getVersion());
		
		
	}

}
